stylesheets.content:
.Datetable-module_calendar-table__kXuUW {
  border-collapse: collapse;
  width: 100%;
}

.Datetable-module_calendar-cell__AYa1P {
  width: 14.2857142%;
  background: #f5f5f5;
  border: 1px solid #ddd;
  color: #666;
  font-size: 12px;
  text-align: right;
  vertical-align: middle;
  padding: 0;
  border-collapse: collapse;
  cursor: pointer;
  height: 25px;
}

.Datetable-module_calendar-head__hLis- {
  height: 25px;
  background: #f1f1f1;
  font-weight: 700;
  text-align: center;
  color: #999;
  cursor: default;
}

.Option-module_select-label__dUYnE {
  display: inline;
  position: relative;
  z-index: 9999;
  margin: 0;
  padding: 5px 3px;
  font-size: 14px;
  line-height: 20px;
  font-weight: bold;
  background-color: #fff;
  float: left;
  width: 182px;
  text-align: center;
  cursor: pointer;
}
.Option-module_select-label__dUYnE:hover span {
  text-decoration: underline;
}
.Option-module_select-label__dUYnE:hover i {
  opacity: 1;
}

.Option-module_select__cFv3Q {
  border: 1px solid #ccc;
  position: absolute;
  right: 0;
  top: 30px;
  z-index: 101;
  display: none;
  background: #fff;
  overflow-y: hidden;
}
.Option-module_select__cFv3Q.Option-module_active__l1JFp {
  display: block;
}

.Option-module_options__-3Tfq {
  max-height: 140px;
  overflow-y: auto;
}
.Option-module_options__-3Tfq::-webkit-scrollbar {
  width: 7px;
}
.Option-module_options__-3Tfq::-webkit-scrollbar-track {
  border-radius: 3px;
}
.Option-module_options__-3Tfq::-webkit-scrollbar-thumb {
  background: #ccc !important;
  height: 20px;
  border-radius: 3px;
}

.Option-module_icons__vqJQw {
  background-image: url(data:image/png;base64,);
  opacity: 0.5;
  background-position: -92px -19px;
  display: inline-block;
  width: 9px;
  height: 20px;
  vertical-align: middle;
}

.Option-module_month__OVX8e {
  width: 100px;
  text-align: right;
}
.Option-module_month-select__oHEfm {
  right: -7px;
}

.Option-module_year__8IIvL {
  width: 48px;
  margin-left: 5px;
}
.Option-module_year-select__h0gX- {
  right: 2px;
}

.DateItem-module_calendar-cell__pKEkZ {
  width: 14.2857142%;
  background: #f5f5f5;
  border: 1px solid #ddd;
  color: #666;
  font-size: 12px;
  text-align: right;
  vertical-align: middle;
  padding: 0;
  border-collapse: collapse;
  cursor: pointer;
  height: 25px;
}

.DateItem-module_calendar-date__EKQ7S:hover {
  color: #fff !important;
  background: #ff8000 !important;
  box-shadow: none !important;
}
.DateItem-module_calendar-date__EKQ7S div {
  padding-right: 5px;
}
.DateItem-module_calendar-date__EKQ7S.DateItem-module_today__twreL {
  color: #33aaff;
}
.DateItem-module_calendar-date__EKQ7S.DateItem-module_active__NYXDI {
  background: #33aaff;
  box-shadow: #178fe5 0 1px 3px 0 inset;
  color: #fff;
  font-weight: 700;
}
.DateItem-module_calendar-date__EKQ7S.DateItem-module_other-month__9wISU {
  opacity: 0.5;
  cursor: default;
}

.NavButton-module_icons__5PIwJ {
  background-image: url(data:image/png;base64,);
  opacity: 0.5;
  background-position: -92px -19px;
  display: inline-block;
  width: 9px;
  height: 20px;
  vertical-align: middle;
}

.NavButton-module_nav-btn__cI2wz {
  background-color: transparent;
  background-repeat: no-repeat;
  border: 0 none;
  cursor: pointer;
  display: block;
  height: 30px;
  opacity: 0.5;
  outline: medium none;
  overflow: hidden;
  padding: 0;
  position: relative;
  text-indent: 100%;
  white-space: nowrap;
  width: 20px;
  min-width: 0;
}
.NavButton-module_nav-btn__cI2wz:hover {
  opacity: 1;
}

.NavButton-module_prev-btn__B-76c {
  float: left;
  background-position: -20px 0;
}

.NavButton-module_today-btn__4YWv- {
  float: left;
  background-position: -70px 0;
  margin-left: 5px;
}

.NavButton-module_next-btn__kbWpM {
  float: right;
  background-position: 0 0;
}

.Datepicker-module_datepicker__rlkh- {
  box-shadow: 0 5px 15px -5px rgba(0, 0, 0, 0.506);
  background: #fff;
  border-bottom: 1px solid #bbb;
  border-left: 1px solid #ccc;
  border-right: 1px solid #ccc;
  border-top: 1px solid #ccc;
  color: #333;
  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
  padding: 8px;
  padding-left: 0;
  padding-top: 2px;
  position: relative;
  width: 228px;
  z-index: 9999;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}
.Datepicker-module_datepicker__rlkh- * {
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  padding: 0;
  margin: 0;
}
.Datepicker-module_datepicker__rlkh-.Datepicker-module_portal__p0cCB {
  position: absolute;
}

.Datepicker-module_option__Rjnnl {
  padding: 2px 10px 2px 5px;
  text-decoration: none !important;
}
.Datepicker-module_option__Rjnnl:hover {
  color: #fff;
  background: #ff8000;
}
.Datepicker-module_option__Rjnnl.Datepicker-module_active__4GG-M {
  background: #33aaff;
  box-shadow: #178fe5 0 1px 3px 0 inset;
  color: #fff;
  font-weight: 700;
}

.Datepicker-module_calendar__I5Thn {
  clear: both;
}

.react-dropdown-select-clear{display:none}

